import React from 'react'
import Core from './components/Core'
import './assests/main.css'
import './assests/background.png'

class App extends React.Component {

    componentDidMount() {
        this.canvas = this.refs.canvas
        this.ctx = canvas.getContext("2d")

        this.ctx.font = "40px Courier"
        this.ctx.fillStyle = "red";
        this.ctx.textAlign = "center";
        this.ctx.fillText('KEYBOARD HERO', 512, 200)

        setInterval(
            () => this.textFlash(),
            2000
        )

    }

    textFlash() {
        this.ctx.fillStyle = '#ffffff'; // or whatever color the background is.
        this.ctx.fillText('PRESS START TO PLAY', 512, 300);
        this.ctx.fillStyle = '#000000'; // or whatever color the text should be.
        this.ctx.fillText('PRESS START TO PLAY', 512, 300);

    }

    render() {
        return(
            <div>
                <canvas ref="canvas" width={1024} height={600} />
            </div>
        )
    }
}

export default App
