import React from 'react'
import GameOver from './GameOver'
import GameScreen from "./GameScreen";

class Core extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            timer: 20000,
            score: 0,
            letters: ['a', 'b', 'c'],
            level: 1,
            failed: false,
            complete: false,
            page: 0
        }
    }

    componentDidMount() {
        let canvas = this.refs.canvas
        this.ctx = canvas.getContext("2d");

        this.listener()
        this.startScreen()
    }

    listener() {
        let self = this

        window.addEventListener("keypress", function (event) {

            // Player win level
            if (self.state.letters.length === 1) {
                self.setState({
                    level: self.state.level + 1,
                    complete: true,
                    timer: self.state.timer - 2000
                })
            }

            // Checks key press and updates score
            if (self.state.letters.includes(event.key)) {

                let index = self.state.letters.indexOf(event.key)
                self.state.letters.splice(index, 1)

                self.setState({
                    score: self.state.score + 10
                })
            } else if (!self.state.letters.includes(event.key) && self.state.complete === false ) {
                self.setState({
                    score: self.state.score - 10
                })
            }

            // Continues to text round
            if (event.key === '.') {
                self.state.complete = false

                let newTime = self.state.timer - 2000
                self.setState({
                    timer: newTime
                })

                self.startTimer(newTime)
                self.update_letter()
            }

            // Game screen start
            if (event.code === 'Space' && self.state.page === 0) {
                self.setState({
                    page: 1
                })

                self.clearScreen()
                self.gameScreen()
                this.startTimer(this.state.timer)
            }
        })
    }

    clearScreen() {
        this.ctx.clearRect(0, 0, 1024, 600);
    }

    startScreen() {
        this.ctx.font = "40px Courier"
        this.ctx.fillStyle = "red";
        this.ctx.textAlign = "center";
        this.ctx.fillText('KEYBOARD HERO', 512, 200)

        this.ctx.fillStyle = "blue";
        this.ctx.fillText('PRESS SPACE TO PLAY', 512, 300)
    }

    gameScreen() {
        this.scoreAssest()

        this.ctx.strokeStyle = "#ffffff";
        this.ctx.lineWidth = 1;
        this.ctx.moveTo(0, 500);
        this.ctx.lineTo(1024, 500);
        this.ctx.stroke();
    }

    scoreAssest() {
        this.ctx.font = "20px Courier"
        this.ctx.fillStyle = "white";
        this.ctx.textAlign = "right";
        this.ctx.fillText(`Score: ${this.state.score}`, 1018, 30)
    }

    startTimer(time) {
        this.timerLoop = setInterval(
            () => this.checkStatus(),
            time
        )
    }

    checkStatus() {
        if (this.state.letters.length !== 0) {
            this.setState({
                failed: true
            })

            clearInterval(this.timerLoop)
        }
    }

    update_letter() {
        const keys = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z']

        let next_letters = []

        let i = 0
        for (i = 0; i < 3; i++) {
            const next_letter = keys[Math.floor(Math.random() * keys.length)]
            next_letters.push(next_letter)
        }

        this.setState({
            letters: next_letters
        });
    }

    render() {
        let failed;
        let game;
        let continue_;

        if (this.state.failed) {
            failed = <GameOver />;
        } else if (this.state.complete) {
            continue_ = <h1>Press "space" to continue!</h1>
        } else {
            game = <GameScreen props={this.state}/>
        }

        return (
            <div>
                <h1>Kill the dragon!</h1>

                {game}
                {continue_}
                {failed}

                <h3>Time: {this.state.timer}</h3>

                <canvas ref="canvas" width={1024} height={600} />

            </div>
        )
    }
}

export default Core
