import React from 'react'
import Listener from './Listener'

class Core extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date(), letter: ""};
    }

    componentDidMount() {

        setInterval(
            () => this.tick(),
            1000
        );

        setInterval(
            () => this.next_combo(),
            2000
        );

        this.listener()
    }

    tick() {
        this.setState({
            date: new Date()

        });
    }

    next_combo() {
        const keys = ['l', 'w', 'p', 'a', 'q']
        const next_letter = keys[Math.floor(Math.random() * keys.length)];

        this.setState({
            letter: next_letter
        });
    }

    listener(){
        window.addEventListener("keypress", function(event) {
            console.log(event.key)
        }, true);
    }

    render() {
        return (
            <div>
                <h1>Hello, world!</h1>
                <h2>It is {this.state.date.toLocaleTimeString()}.</h2>
                <h2>Quick! Press: {this.state.letter}</h2>

            </div>
        )
    }
}

export default Core
