import React from 'react'
import Listener from './Listener'

class Core extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date(), score: 0, letters: ['']};
    }

    componentDidMount() {

        var self=this

        setInterval(
            () => this.tick(),
            1000
        );

        setInterval(
            () => this.update_letter(),
            2000
        );

        window.addEventListener("keypress", function(event){
            if (event.key == self.state.letter) {
                console.log("Good job!")

                self.setState({
                    score: self.state.score + 10
                });
            }
        })
    }

    tick() {
        this.setState({
            date: new Date()
        });
    }

    update_letter() {
        const keys = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z']

        next_letters = []
        for (i = 0; i < 3; i++) {
            const next_letter = keys[Math.floor(Math.random() * keys.length)]
            next_letters.append(next_letter)
        }



        this.setState({
            letter: next_letter
        });
    }


    render() {
        return (
            <div>
                <h1>Hello, world!</h1>
                <h2>It is {this.state.date.toLocaleTimeString()}.</h2>

                <h2>1st: {this.state.letters[0]}</h2>
                <h2>2st: {this.state.letters[1]}</h2>
                <h2>3st: {this.state.letters[2]}</h2>

                <h3>Score: {this.state.score}</h3>
            </div>
        )
    }
}

export default Core
