import React from 'react'
import Listener from './Listener'

class Core extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date(), score: 0, letters: []};
    }

    componentDidMount() {

        var self = this

        setInterval(
            () => this.tick(),
            1000
        );

        setInterval(
            () => this.update_letter(),
            5000
        );

        window.addEventListener("keypress", function(event){
            console.log(self.state.letters)

            if (self.state.letters.length == 0) {
                console.log('Good job!')
            }

            if (self.state.letters.includes(event.key)) {

                let index = self.state.letters.indexOf(event.key)
                self.state.letters.slice(0, index).concat(self.state.letters.slice(-index));

                self.setState({
                    score: self.state.score + 10
                });
            } else {
                self.setState({
                    score: self.state.score - 10
                });
            }
        })
    }

    tick() {
        this.setState({
            date: new Date()
        });
    }

    update_letter() {
        const keys = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z']

        let next_letters = []

        let i = 0
        for (i = 0; i < 3; i++) {
            const next_letter = keys[Math.floor(Math.random() * keys.length)]
            next_letters.push(next_letter)
        }

        this.setState({
            letters: next_letters
        });
    }


    render() {
        return (
            <div>
                <h1>Hello, world!</h1>
                <h2>It is {this.state.date.toLocaleTimeString()}.</h2>


                {this.state.letters.map((value, index) => {
                    return <h2 key={index}>{value}</h2>
                })}

                <h3>Score: {this.state.score}</h3>
            </div>
        )
    }
}

export default Core
