import React from 'react'
import GameOver from './GameOver'

class Core extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            startTime: new Date().getTime(),
            score: 0,
            letters: ['a', 'b', 'c'],
            level: 1,
            failed: false,
            complete: false
        };
    }

    componentDidMount() {

        let self = this


    }

    componentDidUpdate() {
        let self = this

        setInterval(
            () => this.checkTimer(),
            10000
        );

        setInterval(
            () => this.update_letter(),
            10000
        );

        if (self.state.failed === false) {
            setInterval(
                () => this.checkTimer(),
                10000
            );

            setInterval(
                () => this.update_letter(),
                10000
            );


            window.addEventListener("keypress", function (event) {

                if (self.state.letters.length === 1) {
                    self.setState({
                        level: self.state.level + 1,
                        complete: true
                    });
                }

                if (self.state.letters.includes(event.key)) {

                    let index = self.state.letters.indexOf(event.key)
                    self.state.letters.splice(index, 1)

                    self.setState({
                        score: self.state.score + 10
                    });
                } else if (!self.state.letters.includes(event.key) && self.state.complete === false ) {
                    self.setState({
                        score: self.state.score - 10
                    });
                }
            })
        }
    }

    checkTimer() {
        let self = this

        console.log(self.state.letters.length)
        if (self.state.letters.length !== 0) {
            self.setState({
                failed: true
            });

            console.log('failed!')
        } else {
            self.setState({
                complete: false
            });
            console.log('safe!')
        }


    }

    update_letter() {
        const keys = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z']

        let next_letters = []

        let i = 0
        for (i = 0; i < 3; i++) {
            const next_letter = keys[Math.floor(Math.random() * keys.length)]
            next_letters.push(next_letter)
        }

        this.setState({
            letters: next_letters
        });
    }

    render() {
        let failed;
        if (this.state.failed) {
            failed = <GameOver />;
        } else {

        }

        return (
            <div>
                {this.state.letters.map((value, index) => {
                    return <h2 key={index}>{value}</h2>
                })}

                <h3>Score: {this.state.score}</h3>
                <h3>Level: {this.state.level}</h3>

                {failed}
            </div>
        )
    }
}

export default Core
