import React from 'react'
import Core from './components/Core'
import './assests/main.css'
import './assests/background.png'

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            page: 0
        }
    }

    componentDidMount() {
        let canvas = this.refs.canvas
        this.ctx = canvas.getContext("2d");

        this.listener()
        this.startScreen()

    }

    listener() {
        let self = this

        window.addEventListener("keypress", function (event) {
            if (event.code === 'Space') {
                self.clearScreen()
                self.gameScreen()
            }
        })
    }

    clearScreen() {
        this.ctx.clearRect(0, 0, 1024, 600);
    }

    startScreen() {
        this.ctx.font = "40px Courier"
        this.ctx.fillStyle = "red";
        this.ctx.textAlign = "center";
        this.ctx.fillText('KEYBOARD HERO', 512, 200)

        this.ctx.fillStyle = "blue";
        this.ctx.fillText('PRESS SPACE TO PLAY', 512, 300)
    }

    gameScreen() {
        this.ctx.moveTo(0, 10);
        this.ctx.lineTo(200, 100);
        this.ctx.stroke();
    }


    render() {
        return(
            <div>
                <canvas ref="canvas" width={1024} height={600} />
            </div>
        )
    }
}

export default App
