import React from 'react'
import GameOver from './GameOver'
import GameScreen from "./GameScreen"
import dragon from '../dragon.gif'

class Core extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            timer: 10000,
            score: 0,
            letters: ['a', 'b', 'c'],
            level: 1,
            failed: false,
            complete: false,
            start: false,
            page: 0
        }
    }

    componentDidMount() {
        let canvas = this.refs.canvas
        this.ctx = canvas.getContext("2d");

        this.listener()
        this.startScreen()
    }

    listener() {
        let self = this

        window.addEventListener("keypress", function (event) {

            // Player win level
            if (self.state.letters.length === 1) {
                self.setState({
                    level: self.state.level + 1,
                    complete: true,
                })
                self.refreshScreen()
            }

            // Checks key press and updates score
            if (self.state.letters.includes(event.key) && self.state.failed === false) {

                let index = self.state.letters.indexOf(event.key)
                self.state.letters.splice(index, 1)

                self.setState({
                    score: self.state.score + 10
                })

                self.refreshScreen()

            } else if (!self.state.letters.includes(event.key) && self.state.complete === false && event.key !== '.' && event.code !== 'Space') {
                console.log(event.key)
                self.setState({
                    score: self.state.score - 10
                })

                self.refreshScreen()
            }

            // Continues to text round
            if (event.key === '.' && self.state.complete === true && self.state.failed === false) {
                self.state.complete = false

                clearInterval(self.timerLoop)
                self.startTimer()
                self.update_letter()
                self.refreshScreen()
            }

            // Game screen start
            if (event.code === 'Space' && self.state.page === 0) {
                self.setState({
                    start: true,
                    page: 1
                })

                self.clearScreen()
                self.gameScreen()
                self.startTimer()
            }
        })
    }

    clearScreen() {
        this.ctx.clearRect(0, 0, 1024, 600);
    }

    refreshScreen() {
        this.clearScreen()
        this.gameScreen()
    }

    startScreen() {
        this.ctx.font = "40px Courier"
        this.ctx.fillStyle = "red";
        this.ctx.textAlign = "center";
        this.ctx.fillText('KEYBOARD HERO', 512, 200)

        this.ctx.fillStyle = "blue";
        this.ctx.fillText('PRESS SPACE TO PLAY', 512, 300)
    }

    gameScreen() {
        this.scoreAssest()

        this.ctx.strokeStyle = "#ffffff"
        this.ctx.lineWidth = 1
        this.ctx.moveTo(0, 500)
        this.ctx.lineTo(1024, 500)
        this.ctx.stroke()

        this.spawnLetters()

        let img = this.refs.dragon
        this.ctx.drawImage(img, 120, 10);
    }

    scoreAssest() {
        this.ctx.font = "20px Courier"
        this.ctx.fillStyle = "white"
        this.ctx.textAlign = "right"
        this.ctx.fillText(`Score: ${this.state.score}`, 1018, 30)
        this.ctx.textAlign = "left"
        this.ctx.fillText(`Level: ${this.state.level}`, 10, 30)
    }

    spawnLetters() {
        let self = this

        let counter = 100
        this.state.letters.forEach(function (item, index) {
            let x = Math.floor(Math.random() * (500 - 10 + 1)) + 10
            let y = Math.floor(Math.random() * (500 - 10 + 1)) + 10

            counter+=50
            self.ctx.fillText(item, x, counter)
        });

    }

    startTimer() {
        this.timerLoop = setInterval(
            () => this.checkStatus(),
            10000
        )
    }

    checkStatus() {
        if (this.state.letters.length !== 0) {
            this.setState({
                failed: true
            })

            clearInterval(this.timerLoop)
        }
    }

    update_letter() {
        const keys = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z']

        let next_letters = []

        let i = 0

        let lettersAmount = {
            1: 3,
            2: 3,
            3: 4,
            4: 5,
            5: 5,
            6: 6,
            7: 9,
            8: 10,
            9: 10,
            10: 12
        }

        for (i = 0; i < lettersAmount[this.state.level]; i++) {
            const next_letter = keys[Math.floor(Math.random() * keys.length)]
            next_letters.push(next_letter)
        }

        this.setState({
            letters: next_letters
        });
    }

    render() {
        let failed;
        let game;
        let continue_;

        if (this.state.failed) {
            this.clearScreen()
            this.scoreAssest()

            this.ctx.font = "40px Courier"
            this.ctx.fillStyle = "red";
            this.ctx.textAlign = "center";
            this.ctx.fillText('GAME OVER', 512, 300)

        } else if (this.state.complete) {
            continue_ = <h1>Press "space" to continue!</h1>
        } else if (this.state.start){
            game = <GameScreen props={this.state}/>
        }

        return (
            <div>
                <canvas ref="canvas" width={1024} height={600} />
                <img ref="dragon" src={dragon} style={{display: 'none'}}/>
            </div>
        )
    }
}

export default Core
